define('view/mobile/modules/work/icons/icon_spinner_view',[
    'jquery',
    'underscore',
    'backbone',
    'TweenMax',
    'view/common/base_view'

], function(

    $,
    _,
    Backbone,
    TweenMax,
    BaseView

) {

    'use strict';

    return BaseView.extend({

        index: null,

        w: 0,
        h: 0,

        isPlaying: false,

        className: 'work-item__spinner js-work-item-spinner',

        // setup ---------------------------------------------------------------

        initialize: function(options) {

            _.bindAll(this, 
                'onAnimationComplete'
            );

            this.render();

            this.cnv = document.createElement('canvas');
            this.ctx = this.cnv.getContext('2d');
            this.ctx.imageSmoothingEnabled = true;
            
            this.$parent = options.$parent;
            this.$cnv = $(this.cnv);
            this.$el.append(this.cnv);

            this.$parent.append(this.el);
            
            this.setDefault();
            this.addEvents();
        },

        setDefault: function() {
            TweenMax.killTweensOf(this.tweenObj);
            this.tweenObj = { 'prCRotate': 0, 'prCMask': 0, 'prCMask2': 0 };
        },

        addEvents: function() {},

        removeEvents: function() {},

        start: function() {

            TweenMax.killTweensOf(this.tweenObj);

            this.isPlaying = true;

            this.setDefault();
            this.loopAnimation();
        },

        stop: function() {
            this.isPlaying = false;
        },

        loopAnimation: function() {

            this.tweenObj = { 'prCRotate': 0, 'prCMask': 0, 'prCMask2': 0 };

            TweenMax.to(this.tweenObj, 0.8, { 'prCMask': 1, 'ease': 'Sine.easeInOut' });
            TweenMax.to(this.tweenObj, 0.8, { 'prCMask2': 1, 'ease': 'Sine.easeInOut', 'delay': 0.8, 'onComplete': this.onAnimationComplete });

            TweenMax.to(this.tweenObj, 1.55, { 'prCRotate': 1, 'ease': 'Linear.easeNone' });  
        },

        onAnimationComplete: function() {
            
            if(this.isPlaying){
                this.loopAnimation();
            }  
        },

        remove: function() {
            this.stop();
            this.$el.remove();
        },

        draw: function() {

            var prCRotate = this.tweenObj.prCRotate;
            var prCMask = this.tweenObj.prCMask;
            var prCMask2 = this.tweenObj.prCMask2;

            var cX = this.w * 0.5;
            var cY = this.h * 0.5;

            var tEndAngle = 1.8 * Math.PI;

            var radius = this.w * 0.5;

            this.ctx.clearRect(-1, -1, this.cnv.width+2, this.cnv.height+2);

            this.ctx.save();

            this.ctx.translate(cX, cY);
            this.ctx.rotate((Math.PI * 2) * prCRotate);
            this.ctx.translate(-cX, -cY);

            this.ctx.beginPath();
            this.ctx.arc( cX, cY, radius - 1, tEndAngle * prCMask2, tEndAngle * prCMask  );
            this.ctx.lineWidth = 1;
            this.ctx.strokeStyle = '#fff';
            this.ctx.stroke();

            this.ctx.restore();
        },

        // update ---------------------------------------------------------------
            
        update: function() {

            if(!this.isPlaying) {
                return;
            }
            this.draw();
        },

        // handlers ---------------------------------------------------------------

        onResize: function(w, h) {

            var pixelRatio = this.pixelRatio = window.devicePixelRatio;
            var iconSize = 18;

            this.w = iconSize;
            this.h = iconSize;

            this.cnv.width = pixelRatio * this.w;
            this.cnv.height = pixelRatio * this.h;

            this.ctx.scale(pixelRatio, pixelRatio);

            this.$el.css({ 'position': 'absolute', 'width': this.w, 'height': this.h });
            this.$cnv.css({ 'position': 'absolute', 'left': 0, 'top': 0, 'width': this.w, 'height': this.h });
        }
    });
});
