            this.el.style.height = parseInt( this.itemHeight, 10 ) + "px";

            // if ( this.isGIF ) {

            //     this.resizeGifCanvas();
            // }

            this.touch.maxScale = Math.min( ( this.model.src_width * 0.5 ) / this.containerWidth, this.touch.MAXIMUM_SCALE );
