define('view/mobile/modules/work/project/carousel/mobile_project_carousel_item_view',[

    'jquery',
    'underscore',
    'backbone',
    'config',
    'TweenMax',
    'util/resn/animation',
    'view/common/base_view'

], function(

    $,
    _,
    Backbone,
    Config,
    TweenMax,
    AnimationUtils,
    BaseView

) {
    'use strict';

    return BaseView.extend({


        index : null,

        image : null,
        src   : '',

        itemWidth: 0,
        itemHeight: 0,


        // setup ---------------------------------------------------------------

        initialize: function(options){

            this.tweenObj = {
                'progress': 0,
                'opacity': 0
            };

            this.src = options.src;

            this.loadImage();
        },

        loadImage: function() {

            this.image        = new Image();

            this.image.onload = this.onImageLoad.bind(this);
            this.image.src    = Config.CDN + '/' + this.src;
        },

        onImageLoad: function() {

            this.image.onload = null;
            this.$el.append(this.image);
        },

        addEvents: function() {},
        removeEvents: function() {},


        // STATE ---------------------------------------------------------------

        animateIn: function (direction) {

            TweenMax.killTweensOf(this.tweenObj);

            TweenMax.fromTo( this.tweenObj, 0.8, {
                'progress': direction * -1,
                'opacity': 0
            }, {
                'progress': 0,
                'opacity': 1,
                'ease': 'Quart.easeOut',
                'delay': 0.05
            });

            this.$el.css({ 'z-index': 0 });
        },

        animateOut: function (direction) {

            TweenMax.killTweensOf(this.tweenObj);

            TweenMax.fromTo( this.tweenObj, 0.8, {
                'progress': 0,
                'opacity': 1
            }, {
                'progress': direction,
                'opacity': 0,
                'ease': 'Quart.easeOut'
            });

            this.$el.css({ 'z-index': 1 });
        },

        resize: function (itemWidth, itemHeight, zoomWidth, zoomLeft, zoomTop) {

            this.itemWidth = itemWidth;
            this.itemHeight = itemHeight;

            this.image.style.width = zoomWidth + 'px';
            this.image.style.left = zoomLeft + 'px';
            this.image.style.top = zoomTop + 'px';
        },


        // UPDATE --------------------------------------------------------------

        update: function( transX ) {

            this.$el[0].style.opacity = this.tweenObj.opacity;

            var xPos = transX + this.tweenObj.progress * this.itemWidth;

            var translationMatrix = AnimationUtils.getTransformationMatrix(xPos, 0, 0);
            var resultMatrix = AnimationUtils.getResultMatrix([translationMatrix]);
            var matrixString = AnimationUtils.getStringTransform3d(resultMatrix);

            this.applyCSS( this.$el, matrixString );
        },


        // ACCESSORS -----------------------------------------------------------

        getImageEl: function ( jqueryBool ) {

            if ( jqueryBool === true ) {
                return $( this.image );
            }
            else {
                return this.image;
            }
        }

    });
});
