    'view/mobile/modules/work/overview/mobile_overview_project_list_view',[   
        'underscore',
        'jquery',
        'templates/templates',
        'model/app_model',
        'view/common/base_view',
        'view/mobile/modules/work/overview/mobile_overview_project_item_view'
    ],
    function(_, $, Templates, AppModel, BaseView, ItemView) {
        return BaseView.extend({

            _allItems: null,
            _currentItems: null,

            initialize: function(options) {
                this._categories = AppModel.get('categories').toJSON();
                this.parent = options.parent;

                this._allItems = AppModel.get('projects').map(
                    function(project) {
                        return new ItemView({
                            parent: this,
                            model: project,
                            visible: true
                        });
                    }.bind(this)
                );

                this._allItemsCount = this._allItems.length;
            },

            filter: function(options) {
                this._currentItems = _.filter(this._allItems, function(item) {

                    var itemCategories = item.model.get('categories');
                    var containsCategory = _.contains(itemCategories, options.category);
                    var visible = options.category === "all" || containsCategory;

                    if (visible) {
                        item.attach();
                    } else {
                        item.detach();
                    }

                    return visible;

                }, this);

                this.onResize();

                window.requestAnimationFrame(_.bind(function(){
                    this.showSelected(options);
                },this));
            },

            showSelected: function(options) {
                for (var i = 0; i < this._currentItems.length; i++) {
                    this._currentItems[i].show({index: i, delay: 0, isFromHome: options.isFromHome, isFromMenu: options.isFromMenu, isFromProjectDetail: options.isFromProjectDetail});
                }
            },

            hideAll: function(options) {
                for (var i = 0; i < this._allItemsCount; i++) {
                    this._allItems[i].hide(options);
                }
            },

            onResize: function(width,height) {
                width = width ? width : window.innerWidth;
                height = height ? height : window.innerHeight;

                for (var i = 0; i < this._allItemsCount; i++) {
                    this._allItems[i].resize(width,height);
                }
            }
        });
    }
