define(
    'view/mobile/modules/work/mobile_work_overview_view',[   
        'underscore',
        'TweenMax',
        'route/router',
        'model/app_model',
        'templates/templates',
        'view/common/base_view',
        'view/mobile/modules/work/overview/mobile_overview_header_view',
        'view/mobile/modules/work/overview/mobile_overview_category_list_view',
        'view/mobile/modules/work/overview/mobile_overview_project_list_view'
    ],
    function(
        _,
        TweenMax,
        Router,
        AppModel,
        Templates,
        BaseView,
        OverviewHeader,
        CategoryListView,
        ProjectListView
    ) {
        return BaseView.extend({
            visible: false,
            touchStarted: false,

            scrollTop: {y:0},

            initialize: function(options) {

                _.bindAll(this, 'onCloseTouchstart', 'onCloseTouchend');

                this._ui = {
                    frame: this.$('.js-project__frame'),
                    close: this.$('.js-close'),
                    scrollArea: this.$('.work__overview__scroll-area')
                };

                this._projectList = new ProjectListView({
                    el: this.$('.js-overviewProjectList'),
                    parent: this
                });

                this._overviewHeader = new OverviewHeader({
                    el: this.$('.js-overviewHeader')
                });

                this._categoryList = new CategoryListView({
                    el: this.$('.js-overviewCategoryList'),
                    projectList: this._projectList
                });

            },

            _addEvents: function() {
                // this._ui.close.on('click', this._onCloseClick);
                this._ui.close.on('touchstart', this.onCloseTouchstart);
                this._ui.close.on('touchend', this.onCloseTouchend);

                this._categoryList.on( 'requestOpen', this._onCategoryListRequestOpen, this );
                this._categoryList.on( 'requestClose', this._onCategoryListRequestClose, this );
            },

            _removeEvents: function() {
                // this._ui.close.off('click', this._onCloseClick);
                this._ui.close.off('touchstart', this.onCloseTouchstart);
                this._ui.close.off('touchend', this.onCloseTouchend);

                this._categoryList.off( 'requestOpen', this._onCategoryListRequestOpen, this );
                this._categoryList.off( 'requestClose', this._onCategoryListRequestClose, this );
            },

            show: function(options) {
                if (this.visible) {
                    return;
                }
                this.visible = true;

                window.requestAnimationFrame(_.bind(function(){
                    this._ui.scrollArea.scrollTop(0);
                    this._projectList.filter(options);
                },this));

                TweenMax.killTweensOf(this.$el);

                this.el.style.display = "block";

                if(options.isFromProjectDetail) {
                    TweenMax.set(this.$el, {'opacity':1});
                } else {
                    TweenMax.to(this.$el, 1, {'opacity':1, ease: 'Sine.easeInOut'});
                }

                this._addEvents();

                this._overviewHeader.show(options, 0.62);
                this._categoryList.show(options);

                // this.onResize();

                TweenMax.killTweensOf(this._ui.frame);
                TweenMax.to(this._ui.frame, 1, { 'opacity': 1, 'force3D': true, 'ease': 'Cubic.easeOut', 'delay': 0.6 });
            },

            hide: function(options) {
                if (!this.visible) {
                    return;
                }
                this.visible = false;

                TweenMax.killTweensOf(this.$el);

                // if(!options.isToMenu) {
                //     TweenMax.to(this.$el, 0.4, { 'opacity': 0, ease: 'Sine.easeInOut' });
                // }

                TweenMax.killTweensOf(this._ui.frame);
                TweenMax.to(this._ui.frame, 0.4, { 'opacity': 0, 'force3D': true, 'ease': 'Sine.easeIn' });

                // this._categoryList.trigger('requestClose');
                this._onCategoryListRequestClose({category: undefined, hideOverview: true});

                this._overviewHeader.hide(options);
                this._projectList.hideAll(options);
                this._categoryList.hide();

                var delay = options.isToProjectDetail ? 1400 : 1000;

                setTimeout( _.bind( function() {
                    this.$el.css({ display: 'none' });
                }, this), delay);

                this._removeEvents();
            },

            update: function(category) {
                this._categoryList.close(category);
                this._projectList.filter({category: category});

                this.scrollToTop();
            },

            getHeight: function() {
                return 6000;
            },

            onResize: function(width, height) {
                this._projectList.onResize(width,height);
            },

            // Handlers

            // _onCloseClick: function() {
            //     if(this.visible) Router.navigate(AppModel.ROUTES.WORK, true);
            // },

            onCloseTouchstart: function () {
                this.touchStarted = true;
            },

            onCloseTouchend: function () {
                if ( this.touchStarted ) {
                    Router.navigate(AppModel.ROUTES.WORK, true);
                    this.touchStarted = false;
                }
            },

            _onCategoryListRequestOpen: function() {
                this._categoryList.open();
            },

            _onCategoryListRequestClose: function(options) {
                this._categoryList.close();
                this._projectList.showSelected({isFromMenu:false, isFromProjectDetail:false});

                if(!options || !options.hideOverview) {this.scrollToTop();}
            },

            scrollToTop: function() {
                this.scrollTop.y = this._ui.scrollArea.scrollTop();
                TweenMax.to(this.scrollTop, 0.5, {y: 0, ease: 'Expo.easeOut', onUpdate: this.updateScroll.bind(this)});
            },

            updateScroll: function() {
                this._ui.scrollArea.scrollTop(this.scrollTop.y);
            }
        });
    }
);

