            this.$el[0].style.opacity = alpha;

            // var translationMatrix = AnimationUtils.getTransformationMatrix(
            //     0,
            //     0,
            //     tZ
            // );
            // var resultMatrix = AnimationUtils.getResultMatrix([
            //     translationMatrix
            // ]);
            // var matrixString = AnimationUtils.getStringTransform3d(
