            this.$el[0].style.display = 'block';

            TweenMax.to(this.el, 0.5, {delay: 0.1, opacity: 1, ease: 'easeInOutSine'});

            setTimeout(function(){
                Backbone.trigger(AppEvents.Shell.ShowButton);
            }, 500);
